import axios from 'axios';
import './App.css';
import {useEffect, useState} from 'react';
// const axios = require('axios');
function App() {
  const [options , setOptions] = useState([])

const [to , setTo] = useState("en");
const [from , setFrom] = useState("en");
const [output , setOutput] = useState("");
const [input , setInput] = useState("");


useEffect(()=>{


  axios.get('https://libretranslate.com/languages',

  { headers:{'accept':'application/json'}})
  .then(res=>{

    console.log(res.data);
    setOptions(res.data);
  })
},[])


const translate = ()=>{
axios.post('https://libretranslate.de/translate',{
q:input,
source:from,
target:to,
api_key : 'xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx'

},
  {headers:{'accept':'application/json',
'Content-Type':'application/x-www-form-urlencoded'
},
}).then(response => {
  // Handle the translation response here and set the output state
  setOutput(response.data.translatedText);
})


};



  return (
    <div className="App">

      from : ({from})
      <select onChange={e=>setFrom(e.target.value)}>
       {options.map(opt=> <option key={opt.code} value={opt.code}>{opt.name}</option> )}

      </select>

       To :({to})

      <select onChange={e=>setTo(e.target.value)}>
       {options.map(opt=> <option key={opt.code} value={opt.code}>{opt.name}</option> )}

      </select>


      <div>
        <textarea cols="50" rows="8" onInput={(e)=>setInput(e.target.value)}


        ></textarea>
      </div>
      <div>
        <textarea cols="50" rows="8"  value={output} ></textarea>
      </div>
      <div>
        <button onClick={translate}>Translate</button>
      </div>
    </div>
  );
}

export default App;
